mapper.select:
SELECT COUNT(*) > 0
FROM favorites
WHERE post_id = #{postId} AND member_id = #{memberId}

SELECT COUNT(*)
FROM favorites
WHERE post_id = #{postId}

select * from auction_guarantees where post_id=#{postId} 
	and member_id!=#{winnerId} 	
	and status = 'PAID'

mapper.update:
UPDATE posts 
SET view_count = view_count + 1 
WHERE post_id = #{postId}

mapper.select:
SELECT * FROM auction_guarantees
WHERE post_id = #{postId} AND member_id = #{memberId}
LIMIT 1
